import React, { useState } from 'react';
import axios from 'axios';

const Weather = () => {
    const [location, setLocation] = useState('');
    const [weatherData, setWeatherData] = useState(null);
    const [error, setError] = useState(null);

    const apiKey = 'fce4dce34c39c9856ba1800418de95ed';
    const baseUrl = `https://api.openweathermap.org/data/2.5/weather`;

    const getWeatherByLocation = async () => {
        try {
            const response = await axios.get(baseUrl, {
                params: {
                    q: location,
                    appid: apiKey,
                },
            });

            if (response.status === 200) {
                const data = response.data;
                console.log(data);
                setWeatherData(data);
            }
        } catch (err) {
            setError('An error occurred');
        }
    };

    return (
        <div className='weather-display'>
            <h1>Location Weather Search</h1>
            <section className='form-group'>
                <input
                    type="text"
                    placeholder="Enter location"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                />
                <button onClick={getWeatherByLocation} className='btn btn-block'>Search</button>
            </section>

            {weatherData && (
                <div>
                    <h2>{weatherData.weather[0].description}</h2>
                    {/* Image Icon goes here! */}
                    <h2>{weatherData.name}</h2>
                    <p>{weatherData.main.temp}Â°F</p>
                    {/* Add more weather data display here if needed. */}
                </div>
            )}

            {error && <p>{error}</p>}
        </div>
    );
};

export default Weather;
