// ... (other imports)

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    location: '', // Changed from 'zipcode' to 'location'
    password: '',
    confirmPassword: '',
  });

  // ... (other code)

  const onChange = (e) => {
    setFormData((prevState) => ({
      ...prevState,
      [e.target.name]: e.target.value,
    }));
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (password !== confirmPassword) {
      toast.error('Passwords do not match.');
    } else {
      const userData = {
        name,
        email,
        location, // Changed from 'zipcode' to 'location'
        password,
      };
      dispatch(register(userData));
    }
  };

  // ... (other code)
}

export default Register;
